<script>
  import Navbar from '../components/Navbar.vue'
  import Window from '../components/Window.vue'
  import Bubble from '../components/Bubble.vue'
  import Title from '../components/Title.vue'
  import Footer from '../components/Footer.vue'

  import {gsap} from 'gsap/all'
  import ScrollTrigger from 'gsap/ScrollTrigger'

  export default {
    name: 'Index',
    metaInfo: {
      title: 'Digital-Agentur'
    },
    components: {
      Navbar,
      Window,
      Bubble,
      Title,
      Footer
    },
    data() {
      return {
        form: {
          data: {name: '', company: '', email: '', message: '', privacy: false},
          errors: {name: false, company: false, email: false, message: false, privacy: false},
          success: false
        }
      }
    },
    methods: {
      sleep(ms) {
        return new Promise(
          resolve => setTimeout(resolve, ms)
        );
      },  
      clearForm: function() {
        this.form.data.name = ''
        this.form.data.company = ''
        this.form.data.email = ''
        this.form.data.message = ''
        this.form.data.privacy = false
        this.form.errors.name = false
        this.form.errors.company = false
        this.form.errors.email = false
        this.form.errors.message = false
        this.form.errors.privacy = false
      },
      checkName() {
        this.form.errors.name = (this.form.data.name == '' || this.form.data.name.length < 3 || this.form.data.name.indexOf(' ') == -1)
      },
      checkCompany() {
        this.form.errors.company = (this.form.data.company == '' || this.form.data.company.length < 3)
      },
      checkEmail() {
        var re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        this.form.errors.email = !re.test(String(this.form.data.email).toLowerCase());
      },
      checkMessage() {
        this.form.errors.message = (this.form.data.message == '')
      },
      checkPrivacy() {
        this.form.errors.privacy = !this.form.data.privacy
      },
      encode(data) {
        return Object.keys(data)
          .map(key => encodeURIComponent(key) + '=' + encodeURIComponent(data[key]))
          .join('&')
      },
      submitForm(e) {
        this.checkName()
        this.checkCompany()
        this.checkEmail()
        this.checkMessage()
        this.checkPrivacy()
        if(!(this.form.errors.name || this.form.errors.company || this.form.errors.email || this.form.errors.message || this.form.errors.privacy)) {
          fetch('/', {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: this.encode({
              'form-name': e.target.getAttribute('name'),
              ...this.form.data
            }),
          })
          .then(() => {
            this.clearForm()
            this.form.success = true
          })
          .catch(error => console.log(error))
        }
      },
      hover_im() {
        if(!this.tl_im.isActive()) {
          this.tl_im.play(0)
        }
      },
      hover_cp() {
        if(!this.tl_cp.isActive()) {
          this.tl_cp.play(0)
        }
      },
      hover_dk() {
        if(!this.tl_dk.isActive()) {
          this.tl_dk.play(0)
        }
      },
      hover_wd() {
        if(!this.tl_wd.isActive()) {
          this.tl_wd.play(0)
        }
      },
      hover_om() {
        if(!this.tl_om.isActive()) {
          this.tl_om.play(0)
        }
      },
      animations() {
        gsap.to('#app', {opacity: 1, duration: 0.2})
        //loading animation:
        gsap.from('#navbar', {opacity:0, duration: 0.8})
        gsap.fromTo('#header-window', {y: 100, opacity: 0, scale: 0.95}, {y: 0, opacity: 1, scale: 1, duration: 1.6, ease: 'power3'})
        gsap.from('#satro-logo', {y: 100, opacity: 0, scale: 0.95, duration: 1.6, delay: 0.8, ease: 'power3'})
        gsap.from('#nav-item-1', {y: -200, duration: 0.8, delay: 0.3})
        gsap.from('#nav-item-2', {y: -200, duration: 0.8, delay: 0.4})
        gsap.from('#nav-item-3', {y: -200, duration: 0.8, delay: 0.5})
        gsap.from('#nav-item-4', {y: -200, duration: 0.8, delay: 0.6})
        gsap.from('#nav-item-5', {y: -200, duration: 0.8, delay: 0.7})
        gsap.from('#intro', {y: 100, opacity: 0, duration: 1.6, delay: 1.0, ease: 'power3'})
        //scroll reveal animation:
        const elements = gsap.utils.toArray('.scroll-reveal')
        elements.forEach(element => {
          gsap.from(element, {scrollTrigger: {trigger: element}, y: 100, opacity: 0, scale: 0.95, duration: 1.6, ease: 'power3'})
        })
        //parallax scrolling:
        gsap.timeline({scrollTrigger: {trigger: '#portrait-1', scrub: true}}).fromTo('#portrait-1', {y: 20}, {y: -50})
        gsap.timeline({scrollTrigger: {trigger: '#portrait-2', scrub: true}}).fromTo('#portrait-2', {y: 20}, {y: -10})
        gsap.timeline({scrollTrigger: {trigger: '#reference-window', scrub: true}}).fromTo('#reference-window', {y: 50}, {y: -50})
        gsap.timeline({scrollTrigger: {trigger: '#contact-form', scrub: true}}).fromTo('#contact-form', {y: 50}, {y: -50})
        //influencer management icon animation:
        const tl_im_bubble_1 = gsap.timeline()
        .to('#im-bubble-1', 0.3, {x: 3, y: 90, scale: 1.1})
        .to('#im-bubble-1', 0.2, {rotate: -10, transformOrigin: '50% 50%'})
        .to('#im-bubble-1', 1, {rotate: 0, ease: 'elastic', transformOrigin: '50% 50%'})
        .to('#im-bubble-1', 0.3, {x: 11.05, y: 84.971, scale: 1, transformOrigin: '0% 0%', delay: -0.3})
        const tl_im_bubble_2 = gsap.timeline()
        .to('#im-bubble-2', 0.3, {x: 3, y: 5, delay: -0.3})
        .to('#im-bubble-2', 0.2, {rotate: -10, transformOrigin: '50% 50%'})
        .to('#im-bubble-2', 1, {rotate: 0, ease: 'elastic', transformOrigin: '50% 50%'})
        .to('#im-bubble-2', 0.3, {x: 0, y: 0, transformOrigin: '0% 0%', delay: -0.3})
        const tl_im_bubble_3 = gsap.timeline()
        .to('#im-bubble-3', 0.3, {x: 83, y: 55, scale: 1.05, delay: -0.3})
        .to('#im-bubble-3', 0.2, {rotate: -5, transformOrigin: '50% 50%'})
        .to('#im-bubble-3', 1, {rotate: 0, ease: 'elastic', transformOrigin: '50% 50%'})
        .to('#im-bubble-3', 0.3, {x: 90.95, y: 39.936, scale: 1, transformOrigin: '0% 0%', delay: -0.3})
        this.tl_im = gsap.timeline({paused: true})
        .to('#im-bg', {opacity: 0.5})
        .add(tl_im_bubble_1, 0.2)
        .add(tl_im_bubble_2, 0.5)
        .add(tl_im_bubble_3, 0.4)
        .to('#im-bg', 0.3, {opacity: 1, delay: -0.3})
        //content production icon animation:
        const tl_cp_playbutton = gsap.timeline()
        .to('#cp-playbutton', 0.6, {scale: 1.2, transformOrigin: '50% 50%'})
        .to('#cp-playbutton', 0.2, {scale: 1.0, transformOrigin: '50% 50%', ease: 'back'})
        .to('#cp-playbutton', 0.4, {scale: 1.1, transformOrigin: '50% 50%', ease: 'back'})
        .to('#cp-playbutton', 1, {scale: 1, transformOrigin: '50% 50%'})
        const tl_cp_slider = gsap.timeline()
        .to('#cp-slider', 0.3, {y: 68, scale: 1.05})
        .to('#cp-slider', 0.9, {y: 78.036, scale: 1, ease: 'back'})
        const tl_cp_bullhorn = gsap.timeline()
        .to('#cp-bullhorn', 0.3, {y: 25, scale: 1.05})
        .to('#cp-bullhorn', 0.9, {y: 34.777, scale: 1, ease: 'back'})
        const tl_cp_dots = gsap.timeline()
        .to('#cp-dots', 0.3, {y: 79, scale: 1.05})
        .to('#cp-dots', 0.9, {y: 89.063, scale: 1, ease: 'back'})
        this.tl_cp = gsap.timeline({paused: true})
        .to('#cp-bg', {opacity: 0.5})
        .to('#cp-text', {opacity: 0.5, delay: -0.5})
        .add(tl_cp_playbutton, 0)
        .add(tl_cp_slider, 0.8)
        .add(tl_cp_bullhorn, 1)
        .add(tl_cp_dots, 1.2)
        .to('#cp-bg', 0.3, {opacity: 1, delay: -0.9})
        .to('#cp-text', 0.3, {opacity: 1, delay: -0.9})
        //digitale konzepte icon animation:
        const tl_dk_hide_lines = gsap.timeline()
        .to('#dk-line-1', 0.1, {scaleX: 0})
        .to('#dk-line-2', 0.1, {scaleX: 0, delay: -0.1})
        const tl_dk_lines = gsap.timeline()
        .to('#dk-line-1', 1.0, {scaleX: 1})
        .to('#dk-line-2', 0.6, {scaleX: 1, delay: 0.4})
        const tl_dk_pencil = gsap.timeline()
        .to('#dk-pencil', 0.4, {x: -10, y: -1})
        .to('#dk-pencil', 1.0, {x: 145, rotate: 25, transformOrigin: '50% 0%', delay: -0.04})
        .to('#dk-pencil', 0.4, {x: -10, y: 11, rotate: -10})
        .to('#dk-pencil', 0.6, {x: 59.274, rotate: 0})
        this.tl_dk = gsap.timeline({paused: true})
        .to('#dk-bg', {opacity: 0.5})
        .add(tl_dk_hide_lines, 0.0)
        .add(tl_dk_pencil, 0.2)
        .add(tl_dk_lines, 0.6)
        .to('#dk-bg', {opacity: 1, delay: -0.3})
        //webdesign icon animation:
        const tl_wd_layout_1 = gsap.timeline()
        .to('#wd-text', {x: 0, y: 33, scaleX: 1.08})
        .to('#wd-box-1', {x: 43.327, delay: -0.5})
        .to('#wd-box-2', {x: 43.327, delay: -0.3})
        .to('#wd-box-3', {x: 0, y: 1, delay: -0.5})
        .to('#wd-text', {y: 0, scaleX: 1.0, delay: 0.1})
        .to('#wd-box-2', {x: 0, delay: -0.5})
        .to('#wd-box-3', {x: 43.327, y: 32.958, delay: -0.5})
        .to('#wd-text', {x: 60.319, y: 0, delay: 0.1})
        .to('#wd-box-1', {x: 0, y: 0.845, delay: -0.5})
        this.tl_wd = gsap.timeline({paused: true})
        .to('#wd-bg', {opacity: 0.5})
        .add(tl_wd_layout_1, 0.3)
        .to('#wd-bg', {opacity: 1, delay: -0.3})
        //online marketing icon animation:
        const tl_om_mouse = gsap.timeline() //47.577 36.988
        .to('#om-mouse', 0.2, {x: 100})
        .to('#om-mouse', 0.0, {y: -70})
        .to('#om-mouse', 0.0, {x: -120})
        .to('#om-mouse', 0.5, {y: 36.988})
        .to('#om-mouse', 0.8, {x: 47.577, delay: 0.2})
        .to('#om-mouse', 0.2, {scale: 0.97, x: 45, y: 33, transformOrigin: '50% 50%', ease: 'back', delay: 0.1})
        .to('#om-mouse', 0.4, {scale: 1.0, x: 47.577, y: 36.988, transformOrigin: '50% 50%', ease: 'back'})
        const tl_om_ad = gsap.timeline()
        .to('#om-ad', 0.2, {scale: 0.8, transformOrigin: '50% 50%', ease: 'back'})
        .to('#om-ad', 0.4, {scale: 1.0, transformOrigin: '50% 50%', ease: 'back'})
        this.tl_om = gsap.timeline({paused: true})
        .to('#om-bg', {opacity: 0.5})
        .add(tl_om_mouse, 0.2)
        .add(tl_om_ad, 2.0)
        .to('#om-bg', {opacity: 1, delay: -0.2})
        //service animation scroll triggers:
        ScrollTrigger.create({trigger: '#service-im', onEnter: this.hover_im})
        ScrollTrigger.create({trigger: '#service-cp', onEnter: this.hover_cp})
        ScrollTrigger.create({trigger: '#service-dk', onEnter: this.hover_dk})
        ScrollTrigger.create({trigger: '#service-wd', onEnter: this.hover_wd})
        ScrollTrigger.create({trigger: '#service-om', onEnter: this.hover_om})
      }
    },
    async mounted() { 
      gsap.registerPlugin(ScrollTrigger)
      await this.sleep(50)
      this.animations()
    } 
  }
</script>

<style scoped>
  .top-service:hover .top-service-image {
    transform: translateY(-10px);
  }
